Object.Field:
CREATE TRIGGER TI_SNP_SCEN_FOLDER BEFORE INSERT ON SNP_SCEN_FOLDER
FOR EACH ROW
BEGIN
  DECLARE vcount int default 0;
  DECLARE ER_UNIQUE_KEY CONDITION FOR SQLSTATE '45000'; 
  IF (NEW.PAR_I_SCEN_FOLDER IS NOT NULL OR NEW.SCEN_FOLDER_NAME IS NOT NULL  ) THEN
    IF ( NEW.PAR_I_SCEN_FOLDER IS NULL ) THEN    
      SELECT count(*) from SNP_SCEN_FOLDER
      WHERE 
        (SNP_SCEN_FOLDER.PAR_I_SCEN_FOLDER = NEW.PAR_I_SCEN_FOLDER OR (SNP_SCEN_FOLDER.PAR_I_SCEN_FOLDER IS NULL AND NEW.PAR_I_SCEN_FOLDER IS NULL)) 
AND SNP_SCEN_FOLDER.SCEN_FOLDER_NAME = NEW.SCEN_FOLDER_NAME
      INTO vcount;
      IF (vcount > 0) THEN SIGNAL ER_UNIQUE_KEY
        SET MESSAGE_TEXT = ' Duplicate entry for key AK_SCEN_FOLDER', MYSQL_ERRNO = 401;
      END IF;
    END IF;  
  END IF;
END

CREATE TRIGGER TU_SNP_SCEN_FOLDER AFTER UPDATE ON SNP_SCEN_FOLDER
FOR EACH ROW
BEGIN
  DECLARE vcount int default 0;
  DECLARE ER_UNIQUE_KEY CONDITION FOR SQLSTATE '45000'; 
  IF (NEW.PAR_I_SCEN_FOLDER IS NOT NULL OR NEW.SCEN_FOLDER_NAME IS NOT NULL  ) THEN
    IF ( NEW.PAR_I_SCEN_FOLDER IS NULL ) THEN    
      SELECT count(*) from SNP_SCEN_FOLDER
      WHERE 
        (SNP_SCEN_FOLDER.PAR_I_SCEN_FOLDER = NEW.PAR_I_SCEN_FOLDER OR (SNP_SCEN_FOLDER.PAR_I_SCEN_FOLDER IS NULL AND NEW.PAR_I_SCEN_FOLDER IS NULL)) 
AND SNP_SCEN_FOLDER.SCEN_FOLDER_NAME = NEW.SCEN_FOLDER_NAME
      INTO vcount;
      IF (vcount > 1) THEN SIGNAL ER_UNIQUE_KEY
        SET MESSAGE_TEXT = ' Duplicate entry for key AK_SCEN_FOLDER', MYSQL_ERRNO = 401;
      END IF;
    END IF;  
  END IF;
END
