Object.Field:
CREATE TRIGGER TI_SNP_ACTION BEFORE INSERT ON SNP_ACTION
FOR EACH ROW
BEGIN
  DECLARE vcount int default 0;
  DECLARE ER_UNIQUE_KEY CONDITION FOR SQLSTATE '45000'; 
  IF (NEW.I_GRP_ACTION IS NOT NULL OR NEW.ACTION_NAME IS NOT NULL  ) THEN
    IF ( NEW.I_GRP_ACTION IS NULL ) THEN    
      SELECT count(*) from SNP_ACTION
      WHERE 
        (SNP_ACTION.I_GRP_ACTION = NEW.I_GRP_ACTION OR (SNP_ACTION.I_GRP_ACTION IS NULL AND NEW.I_GRP_ACTION IS NULL)) 
AND SNP_ACTION.ACTION_NAME = NEW.ACTION_NAME
      INTO vcount;
      IF (vcount > 0) THEN SIGNAL ER_UNIQUE_KEY
        SET MESSAGE_TEXT = ' Duplicate entry for key AK_ACTION', MYSQL_ERRNO = 401;
      END IF;
    END IF;  
  END IF;
END

