CREATE TRIGGER TI_SNP_USER_OBJ_METH BEFORE INSERT ON SNP_USER_OBJ_METH
FOR EACH ROW
BEGIN
  DECLARE vcount int default 0;
  DECLARE ER_UNIQUE_KEY CONDITION FOR SQLSTATE '45000'; 
  IF (NEW.I_METH IS NOT NULL OR NEW.I_WUSER IS NOT NULL OR NEW.I_CONTEXT IS NOT NULL OR NEW.I_OBJECTS IS NOT NULL OR NEW.I_INSTANCE IS NOT NULL  ) THEN
    IF ( NEW.I_CONTEXT IS NULL OR NEW.I_OBJECTS IS NULL OR NEW.I_INSTANCE IS NULL ) THEN    
      SELECT count(*) from SNP_USER_OBJ_METH
      WHERE 
        SNP_USER_OBJ_METH.I_METH = NEW.I_METH
AND SNP_USER_OBJ_METH.I_WUSER = NEW.I_WUSER
AND (SNP_USER_OBJ_METH.I_CONTEXT = NEW.I_CONTEXT OR (SNP_USER_OBJ_METH.I_CONTEXT IS NULL AND NEW.I_CONTEXT IS NULL)) 
AND (SNP_USER_OBJ_METH.I_OBJECTS = NEW.I_OBJECTS OR (SNP_USER_OBJ_METH.I_OBJECTS IS NULL AND NEW.I_OBJECTS IS NULL)) 
AND (SNP_USER_OBJ_METH.I_INSTANCE = NEW.I_INSTANCE OR (SNP_USER_OBJ_METH.I_INSTANCE IS NULL AND NEW.I_INSTANCE IS NULL)) 
      INTO vcount;
      IF (vcount > 0) THEN SIGNAL ER_UNIQUE_KEY
        SET MESSAGE_TEXT = ' Duplicate entry for key AK_USER_OBJ_METH', MYSQL_ERRNO = 401;
      END IF;
    END IF;  
  END IF;
END

